    public static function tableName()
    {
        return 'easyii_pages';
    }

    public function rules()
    {
        return [
            [['parrent_page_id', 'price_id'], 'integer'],
            ['title', 'required'],
            [['title', 'text'], 'trim'],
            ['title', 'string', 'max' => 128],
            ['slug', 'match', 'pattern' => self::$SLUG_PATTERN, 'message' => Yii::t('easyii', 'Slug can contain only 0-9, a-z and "-" characters (max: 128).')],
            ['slug', 'default', 'value' => null],
            ['slug', 'unique'],
            [['seo_title', 'seo_description', 'seo_keywords', 'seo_h1'], 'safe']
        ];
    }

    public function attributeLabels()
    {
        return [
            'title' => Yii::t('easyii', 'Title'),
            'text' => Yii::t('easyii', 'Text'),
            'slug' => Yii::t('easyii', 'Slug'),
            'parrent_page_id' => 'Родительская страница',
            'price_id' => 'Показать прайс-лист'
        ];
    }

    public function behaviors()
    {
        return [
            'seoBehavior' => SeoBehavior::className(),
            'CreateSlug' => CreateSlug::className(),
            'ml' => [
                'class' => MultilingualBehavior::className(),
                'languages' => Yii::$app->params['availableLanguages'],
                /*[
                    'ru' => 'Russian',
                    'en' => 'English',
                ],*/
                //'languageField' => 'language',
                //'localizedPrefix' => '',
                //'requireTranslations' => false',
                //'dynamicLangClass' => true',
                //'langClassName' => PostLang::className(), // or namespace/for/a/class/PostLang
                'defaultLanguage' => Yii::$app->params['defaultLanguage'],
                'langForeignKey' => 'page_id',
                'tableName' => self::tableName().'_lang',
                'attributes' => [
                    'title', 'text','seo_title', 'seo_description', 'seo_keywords', 'seo_h1'
                ]
            ],
        ];
    }
    public static function dropdownList($exclude = false) {
        $data = [0 => '---'];
        $models = self::find()->all();
        foreach ($models as $page) {
            if ($exclude && $exclude == $page->page_id) continue;
            $data[$page->page_id] = $page->title;
        }
        return $data;
    }

